import { useMutation, useQuery } from "@apollo/client";
import gql from "graphql-tag";
import useForm from "../lib/useForm";
import DisplayError from "./ErrorMessage";
import Form from "./styles/Form";

const SINGLE_PRODUCT_QUERY = gql`
    query SINGLE_PRODUCT_QUERY($id:ID!){
        Product(where: {id:$id}){
            id
            name
            description
            price
        }
    }
`;

const UPDATE_PRODUCT_MUTATION = gql`
    mutation UPDATE_PRODUCT_MUTATION (
            $id:ID!
            $name:String
            $description:String
            $price:Int
        ){
            UpdateProduct(
                id:$id,
                data:{
                    id:$id,name: $name,description:$description,price:$price
                }
            )
                {
                  id
                  name
                  description
                  price
                }
        }
`;


export default function UpdateProduct({id}){
    //We need to get the existing product
    const {data,error,loading} = useQuery(SINGLE_PRODUCT_QUERY,{
        variables:{id},
    });

    console.log(data);
    // We need to get the mutation to update the product
    const [updateProduct, {data:updateData,error:updateError,loading:updateLoading}]= useMutation(UPDATE_PRODUCT_MUTATION);


    //Create State for form inputs
    const { inputs, handleChange, clearForm, resetForm } = useForm(data?.Product); 

    if(loading) return <p>loading. . .</p>

    //We need the form to handle the updates 
    return (
        <Form
          onSubmit={async (e) => {
            e.preventDefault();
            const res = updateProduct(); 
            // Submit the inputfields to the backend:

          }}
        >
          <DisplayError error={error || updateError} />
          <fieldset disabled={updateLoading} aria-busy={updateLoading}>

            <label htmlFor="name">
              Name
              <input
                type="text"
                id="name"
                name="name"
                placeholder="Name"
                value={inputs.name}
                onChange={handleChange}
              />
            </label>
            <label htmlFor="price">
              Price
              <input
                type="number"
                id="price"
                name="price"
                placeholder="price"
                value={inputs.price}
                onChange={handleChange}
              />
            </label>
            <label htmlFor="description">
              Description
              <textarea
                id="description"
                name="description"
                placeholder="Description"
                value={inputs.description}
                onChange={handleChange}
              />
            </label>

            <button type="submit">Update Product</button>
          </fieldset>
        </Form>
      );
    }
