import Form from './styles/Form';
import useForm from '../lib/useForm';
import { useMutation } from '@apollo/client';
import gql from 'graphql-tag';
import Error from './ErrorMessage' ; 

//How password reset works
//Backend will generate a random token and you are emailed a link with this token
//and when you try to reset it you pass email new password and this token
//let me check if this token is in the file for you  and is the same as the token last issued 


const RESET_MUTATION = gql`
  mutation RESET_MUTATION($email: String!,$password:String! ,$token:String!) {
    redeemUserPasswordResetToken(email: $email, token:$token, password:$password) {
      code
      message
    }
  }
`;

export default function Reset({token}) {
  const { inputs, handleChange, resetForm } = useForm({
    email: '',
    password:'',
    token:token
  });
  const [reset, { data, loading ,error}] = useMutation(
    RESET_MUTATION,
    {
      variables: inputs,
    }
  );

  const successfullError = data?.redeemUserPasswordResetToken?.code ?
    data?.redeemUserPasswordResetToken:undefined;

    async function handleSubmit(e) {
    e.preventDefault(); // stop the form from submitting
    console.log(inputs);
    const res = await reset().catch(console.error);
    console.log(res);
    console.log({ data, loading, error });
    resetForm();
    // Send the email and password to the graphqlAPI
  }
  return (
    <Form method="POST" onSubmit={handleSubmit}>
      <h2>Reset Your Password</h2>
      <Error error={error || successfullError} />
      <fieldset>
        {data?.redeemUserPasswordResetToken === null && (
          <p>Success! You can now sign in.</p>
        )}

        <label htmlFor="email">
          Email
          <input
            type="email"
            name="email"
            placeholder="Your Email Address"
            autoComplete="email"
            value={inputs.email}
            onChange={handleChange}
          />
          <label htmlFor="password">Password
        <input type="password" name="password" placeholder="Password" autoComplete="password" value={inputs.password} onChange={handleChange}></input>
        </label>
        </label>
        <button type="submit">Request Reset!</button>
      </fieldset>
    </Form>
  );
}
