import CartStyles from './styles/CartStyles';
import { useUser } from './User';
import Supreme from './styles/Supreme';
import styled from 'styled-components';
import formatMoney from '../lib/formatMoney';
import calcTotalPrice from '../lib/calcTotalPrice';
import { useCart } from '../lib/cartState';
import CloseButton from './styles/CloseButton';

const CartItemStyles = styled.li`
    padding:1rem 0;
    border-bottom:1px solid var(--lightGrey);
    display:grid;
    grid-template-columns:auto 1fr auto;
    img{
        margin-right:1rem;
    }
    h3,p{
        margin:0;
    }
`;

function CartItem({cartItem}){
    const {product} = cartItem;
// Can also do cartItem.product.image.publicUrlTransformed too
    if(!product) return null;

    return <CartItemStyles>
        <img 
        width="100"
        src={product.photo.image.publicUrlTransformed} alt={product.name}/>
        <div>
            <h3>{product.name}</h3>
            <p>{formatMoney(product.price * cartItem.quantity)}
                - 
                <em>{cartItem.quantity} &times; {formatMoney(product.price)} </em>
                 each
            </p>

            <h3></h3>
        </div>
        {cartItem.id}
        </CartItemStyles>;
}

export default function Cart() {
    const me = useUser();
    const {cartOpen, closeCart} = useCart();

    if(!me) return null;

    console.log(me);

    return <CartStyles open={cartOpen}> 
        <header><Supreme>{me.name}'s Cart</Supreme>
        <CloseButton onClick={closeCart}>&times;</CloseButton>

        </header>
        <ul>
            {me.cart.map((cartItem) => (
            <CartItem key={cartItem} cartItem ={cartItem}/>
                ))}
        </ul>
        <footer>
            <p>{formatMoney(calcTotalPrice(me.cart))}</p>
        </footer>
    </CartStyles>
}
