import { createContext } from "react";

const LocalStateContext  = createContext();
const LocalStateProvider  = LocalStateContext.Provider;

function CartStateProvider({children}){
    //This is our own custom provider!. We will store data and functionality in here and anyone can access it via the consumer!

    const cartOpen = true;

    return (<LocalStateProvider value={{cartOpen}}> 
        {children}
    </LocalStateProvider>);
}

//make a custom hook to access the cart local state

function useCart(){
    //We use a consumer here to access the local state
    const all = useContext(LocalStateContext);
    return all;
}

export {CartStateProvider,useCart};
