import Link from 'next/link';
import Nav from './Nav';
import styled from 'styled-components';

const Logo = styled.h`
    font-size:4rem;
    margin-left: 2rem;
    position:relative;
    z-index:2;
    background:red;
    transform:skew(-7deg);
    a {
        color:white;
        text-decoration: none;
        text-transform:uppercase;
        padding:0.5rem 1rem;
    }
`;

export default function Header(){
    return (
    <header>
        <div className="bar">
            <Logo>
            <Link href="/">The Store</Link>
            </Logo>
        </div>
        <div className="sub-bar">
            <p>Search</p>
        </div>
        <Nav/>
  </header>
  );
}
