    return (
    <ItemStyles>
        <img 
        //Nested Chaining if product exists check if photo exists and so on
        src={product?.photo?.image?.publicUrlTransformed} alt= {product.name} />
         <Title>
            <Link href={`/product/${product.id}`}>
                {product.name}
            </Link>
        </Title>
        <PriceTag>{Product.price}</PriceTag>
        </ItemStyles>
    );
}
