import useForm from '../lib/useForm';

export default function CreateProduct(){
    const {name,handleChange} =  useForm();

    return (
        <form>
            <label htmlfor="name">Name 
        <input type="text" id="name" name="name" placeholder="name" value ={name} onChange={e => handleChange(e.target.value)}/>
        </label>
        <label htmlfor="price">Price
        <input type="text" id="price" name="price" placeholder="price" value ={price} onChange={e => handleChange(e.target.value)}/>
        </label>
        </form>
    )
}
