import CartStyles from './styles/CartStyles';
import { useUser } from './User';
import Supreme from './styles/Supreme';
import styled from 'styled-components';

const CartItemStyles = styled.li`
    padding:1rem 0;
    border-bottom:1px solid var(--lightGrey);
    display:grid;
    grid-template-columns:auto 1fr auto;
    img{
        margin-right:1rem;
    }
    h3,p{
        margin:0;
    }
`;

function CartItem({cartItem}){
    const {product} = cartItem;
// Can also do cartItem.product.image.publicUrlTransformed too
    if(!product) return null;

    return <CartItemStyles>
        <img 
        width="100"
        src={product.photo.publicUrlTransformed} alt={product.name}></img>
        {cartItem.id}
        </CartItemStyles>;
}

export default function Cart() {
    const me = useUser();

    if(!me) return null;

    console.log(me);

    return <CartStyles open> 
        <header><Supreme>{me.name}'s Cart</Supreme>
        </header>
        <ul>
            {me.cart.map((cartItem) => (
            <CartItem key={cartItem} cartItem ={cartItem}/>
                ))}
        </ul>
    </CartStyles>
}
